pragma solidity ^0.5.0;

import "openzeppelin-solidity/contracts/token/ERC721/ERC721Full.sol";

contract TokenSales {
  ERC721Full public ntfAddress;
  mapping(uint256 => uint256) public tokenPrice;

  constructor(address _tokenAddress) public{
      ntfAddress=ERC721Full(_tokenAddress);
  }

  function setForSale(uint256 _tokenId, uint256 _price) public {
      address tokenOwner = ntfAddress.ownerOf(_tokenId);
      require(tokenOwner == msg.sender, "caller is not token owner");
      require(_price > 0, "price is zero or lower");
      require(ntfAddress.isApprovedForAll(tokenOwner, address(this)),"token owner did not approve TokenSales contract");
      tokenPrice[_tokenId]= _price;

  }
}
